// Extract email data and prepare for processing
const results = [];

for (const item of $input.all()) {
  const email = item.json;

  // Build attachment list
  const attachments = [];
  if (item.binary) {
    for (const [key, value] of Object.entries(item.binary)) {
      attachments.push({
        fileName: value.fileName || key,
        mimeType: value.mimeType,
        binaryKey: key
      });
    }
  }

  results.push({
    json: {
      emailId: email.id,
      messageId: email.messageId || email.id,
      threadId: email.threadId,
      from: email.from,
      to: email.to,
      subject: email.subject,
      date: email.date,
      snippet: email.snippet,
      textPlain: email.textPlain || '',
      textHtml: email.textHtml || '',
      labels: email.labelIds || [],
      attachments: attachments,
      hasAttachments: attachments.length > 0
    },
    binary: item.binary
  });
}

return results;

// Log duplicate detection
const email = $('Extract Attachments').first().json;

return [{
  json: {
    status: 'duplicate_skipped',
    emailId: email.emailId,
    subject: email.subject,
    from: email.from,
    timestamp: new Date().toISOString(),
    message: 'Email already processed - skipping'
  }
}];

