// Prepare invoice data for storage - WITH Purchase Order
const extractedData = $input.first().json.output;
const originalEmail = $('Extract Attachments').first().json;

return [{
  json: {
    // Email metadata
    email_id: originalEmail.emailId,
    message_id: originalEmail.messageId,
    thread_id: originalEmail.threadId,
    email_from: originalEmail.from,
    email_subject: originalEmail.subject,
    email_date: originalEmail.date,

    // Invoice data
    invoice_number: extractedData.invoice_number || '',
    supplier_name: extractedData.supplier_name || '',
    supplier_email: extractedData.supplier_email || originalEmail.from,
    invoice_date: extractedData.invoice_date || '',
    due_date: extractedData.due_date || '',
    total_amount: extractedData.total_amount || 0,
    currency: extractedData.currency || 'EUR',
    tax_amount: extractedData.tax_amount || 0,
    purchase_order_number: extractedData.purchase_order_number || '',
    line_items: JSON.stringify(extractedData.line_items || []),
    payment_terms: extractedData.payment_terms || '',

    // Processing metadata
    status: 'pending_validation',
    po_status: 'linked',
    confidence_score: extractedData.confidence_score || 0,
    processed_at: new Date().toISOString(),
    needs_review: extractedData.confidence_score < 80,

    // Attachments info
    attachments: JSON.stringify(originalEmail.attachments)
  }
}];

// Prepare invoice data for storage - MISSING Purchase Order
const extractedData = $input.first().json.output;
const originalEmail = $('Extract Attachments').first().json;

return [{
  json: {
    // Email metadata
    email_id: originalEmail.emailId,
    message_id: originalEmail.messageId,
    thread_id: originalEmail.threadId,
    email_from: originalEmail.from,
    email_subject: originalEmail.subject,
    email_date: originalEmail.date,

    // Invoice data
    invoice_number: extractedData.invoice_number || '',
    supplier_name: extractedData.supplier_name || '',
    supplier_email: extractedData.supplier_email || originalEmail.from,
    invoice_date: extractedData.invoice_date || '',
    due_date: extractedData.due_date || '',
    total_amount: extractedData.total_amount || 0,
    currency: extractedData.currency || 'EUR',
    tax_amount: extractedData.tax_amount || 0,
    purchase_order_number: '',
    line_items: JSON.stringify(extractedData.line_items || []),
    payment_terms: extractedData.payment_terms || '',

    // Processing metadata
    status: 'pending_po',
    po_status: 'missing',
    confidence_score: extractedData.confidence_score || 0,
    processed_at: new Date().toISOString(),
    needs_review: true,
    reminder_sent: false,
    reminder_count: 0,

    // Attachments info
    attachments: JSON.stringify(originalEmail.attachments)
  }
}];

// Log successful processing with PO
const invoice = $('Prepare Invoice (with PO)').first().json;

return [{
  json: {
    status: 'success',
    message: 'Invoice processed successfully with linked PO',
    invoice_number: invoice.invoice_number,
    supplier: invoice.supplier_name,
    amount: invoice.total_amount,
    currency: invoice.currency,
    po_number: invoice.purchase_order_number,
    processed_at: new Date().toISOString()
  }
}];
